  }
  return (
    <div className='container'>
      <br /> 
      <h2> What's the plan for today ?</h2>
      <br /> <br />

      <div className='row'>
        <div className='col'>
          <input value={input} onChange={(e)=> setInput(e.target.value)} className='form-control form-control-1g'/>
        </div>
        <div className='col-auto'>
          <button onClick={addTask} className='btn btn-1g btn-success'>Add Task</button>
        </div>
      </div>
      <br/>
      <div className='row'>
        <div className='col'>
          <input className='form-control form-control-1g'/>
        </div>
        <div className='col-auto'>
          <button className='btn btn-1g btn-success mr-20'>Update</button>
          <button className='btn btn-1g btn-warning'>Cancel</button>
        </div>
      </div>
      <br />
      {todos&&todos.length ? '': 'No tasks.....'}
      {todos && todos
        // .sort((a,b) => a.id > b.id ?1 : -1)
        .map((task, index)=> {
          return(
            <React.Fragment key={task.id}>
              <div className='col taskBg'>
                <div className={task.status ? 'done': ''}>
                  <span className='taskNumber'>{index + 1}</span>
                  <span className='taskText'>{task.title}</span>
                </div>
                <div className='iconsWrap'>
                  <span title='Edit' onClick={()=> setUpdateData({ id: task})} >
                    <FontAwesomeIcon icon={faPen} />
                  </span>
                  <span title='Delete' onClick={()=>deleteTask(task.id)} >
                    <FontAwesomeIcon icon={faTrashCan} />
                  </span>
                </div>


              </div>

            </React.Fragment>
          )
        })
      }
    </div>
  );
    }

export default App
