import React from 'react'
import {v4 as uuidv4} from 'uuid'

const Form = ({input, setInput, todos, setTodos}) => {
    const onInputChange = (e)=>{
        setInput(e.target.value);
    }

    const onFormSubmit =(e)=> {
        e.preventDefault()
        setInput([...todos, {id:uuidv4(), title: input, completed:false}])
        set
    }
  return (
    <form onSubmit={onFormSubmit}>
        <input type='text' placeholder='Add a Todo' className='text-input' value={input} required onChange={onInputChange}/>
        <button className='add-button' type='submit'>Add a Todo</button>
    </form>
  )  
}

export default Form
