import React, {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import{
  faPen, faTrashCan
} from '@fortawesome/free-solid-svg-icons'
import './App.css';
import AddTaskForm from './Component/AddTaskForm';
import UpdateForm from './Component/UpdateForm';

function App() {

  const [input, setInput]= useState('');
  const [updateData, setUpdateData] = useState('');
  const [todos, setTodos]= useState([])
  const addTask = ()=>{
    if(input){
      let num = todos.length +1;
      let newEntry = { id:num, title:input, status: true}
      setTodos([...todos, newEntry])
      setInput('');
    }

  }
  const deleteTask = (id)=>{
    let newTask = todos.filter(task => task.id!== id)
    setTodos(newTask)


  }
  const cancelUpdate = () =>{
    setUpdateData('');
  }
  const changeTask=(e) => {
    let newEntry ={
      id: updateData.id,
      title: e.target.value,
      status: updateData.status ? true : false
    }
    setUpdateData(newEntry)
  }
  const updateTask = ()=>{
    let filterRecords = [...todos].filter(task => task.id !==updateData.id)
    let updatedObject = [...filterRecords, updateData]
    setTodos(updatedObject)
    setUpdateData('')
  }
  return (
    <div className='container'>
      <br /> 
      <h2 className='heading'> What's the plan for today ?</h2>
      <br /> <br />
      {updateData && updateData ? (
        <UpdateForm
          updateData={updateData}
          updateTask={updateTask}
          cancelUpdate
          changeTask
        />
      ):(
       <AddTaskForm 
          input ={input}
          setInput ={setInput}
          addTask = {addTask}

       />

      ) }



      {todos&&todos.length ? '': 'No tasks.....'}
      {todos && todos
        .sort((a,b) => a.id > b.id ?1 : -1)
        .map((task, index)=> {
          return(
            <React.Fragment key={task.id}>
              <div className='col taskBg'>
                <div className={task.status ? 'done': ''}>
                  <span className='taskNumber'>{index + 1}</span>
                  <span className='taskText'>{task.title}</span>
                </div>
                <div className='iconsWrap'>
                  <span title='Edit' onClick={()=> setUpdateData({ id: task.id, title: task.title, status: task.status? true:false })} >
                    <FontAwesomeIcon icon={faPen} />
                  </span>
                  <span title='Delete' onClick={()=>deleteTask(task.id)} >
                    <FontAwesomeIcon icon={faTrashCan} />
                  </span>
                </div>


              </div>

            </React.Fragment>
          )
        })
      }
    </div>
  );
    }

export default App
